    function fn() {
        var a = 1
        return function fn2() {//这个是闭包
            var b = 1
            return function fn3() {//这个是闭包
                console.log(++b);
            }
        }
    }
    var afn = fn()() //实际这里就多加的括号
    afn()//2
    afn()//3

//闭包
//闭包在使用的时候，能暂时性的存储数据到内存里面，如果是低版本浏览器（ie5-ie7）
//就会出现内存泄漏问题，导致越来越卡，而新版本的浏览器比如chrome就不会有这种问题
function fn(){
  let a = 1
  function fn2(){
    return ++a
  }
  return fn2
}
let f = fn()
console.log(f())//2
console.log(f())//3
console.log(f())//4

